package Registro_L;

import javax.swing.JOptionPane;

public class ES {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}
	public static int PI(int a,String p){
		return a = Integer.parseInt
				(JOptionPane.showInputDialog(p));
	}
	public static double PD(double x,String p){
		return x = Double.parseDouble
				(JOptionPane.showInputDialog(p));
	}
	public static long PL(long y,String p){
		return y = Long.parseLong
				(JOptionPane.showInputDialog(p));
	}
	public static void md(String p){
		JOptionPane.showMessageDialog(null, p);
	}
	public static String PS(String p){
		return p = JOptionPane.showInputDialog(p);
	}
}
